(763, 'Bửu tiết', 'Mới mua hôm qua rất hài lòng về hãng này.đầy đủ các tính năng tôi cần như: nghe gọi to rõ, tự động ghi âm cuộc gọi, phông chữ to rõ, pin dung lượng lớn bàn phím dễ bấm, có chức năng hạn chế thời gian gọi.cảm ơn tgdđ đã giới thiệu sản phẩm này.', '', '5', 143, '2020-04-11 08:06:08', NULL, NULL),
(777, 'Nguyễn Nhựt Huy', 'Điện thoại đẹp ... chất lượng tốt ... mà hình to đẹp rõ sắt nét ... phím nhấn nhẹ và phản hồi nhanh ... giá bình dân hợp túi tiền ... cám mơn nhân viên thế giới di động phục vụ rất nhiệt tình ....', '', '5', 142, '2020-04-11 08:06:08', NULL, NULL),
(779, 'Phạm Nhựt', 'Màn hình 2.8 inch, có phím nhanh đèn pin như vậy là tuyệt lắm rồi. Chưa biết Masstel IZI 280 có độ bền cỡ nào nhưng trước mắt mình cho 5* vì giá chỉ có 350k.', '', '5', 142, '2020-04-11 08:06:08', NULL, NULL),
(813, 'Thuận', 'Sản phẩm giá hợp lí. Dễ sử dụng. Chất lượng phục vụ của nhân viên thì rất tốt. Rất thích mua hàng tại đây. Sẽ luôn ủng hộ.', '', '4', 141, '2020-04-11 08:06:08', NULL, NULL),
(814, 'Nhat', 'Mới mua ở DMX nhân viên nhiệt tình điện thoại nghe gọi ok, pin tốt, đánh giá 5* cho chất lượng', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(815, 'Lê đức thành', 'Máy đẹp. Pin tốt. Nghe gọi rõ ok. Nhân viên phục vụ tận tình. Mai mốt mua thêm chục máy về dùng dần', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(816, 'Đỗ Minh Quân', 'sản phẩm tốt pin trâu thiết kế nhỏ gọn đẹp,nhân viên của TGDĐ rất nhiệt tình', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(817, 'Hoàng Thị Vinh', 'Nói chung là tốt, đáp ứng đủ nhu cầu nghe gọi của tôi.', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(818, 'Bùi văn tính', 'Máy ngon bổ rẻ lướt Web chơi game ngon nghe gọi loa rất to đáng túi tiền rất đáng mua trọng tầm giá', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(819, 'Ngô văn Mùi', 'Rất ưng sp nhỏ nhẹ loa nghe gọi ngon lành cầm đọc sợ rơi nhân viên phục vụ chu đáo nhiệt tình mua cửa hàng TGDD phố nỉ', '', '4', 141, '2020-04-11 08:06:08', NULL, NULL),
(820, 'Đức Hải', 'Thiết kế đẹp nhỏ nhẹ pin lâu phù hợp cho mọi người ...nhân viên TGDĐ nhiệt tình tất cả đều OK .', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(821, 'Lâm Văn Bé Nhỏ', 'Tuyệt vời, nhân viên ở TGDD ở Tây Ninh rất nhiệt tình và chu đáo , khách hàng thân thiết được giảm 15% , hihiRất hài lòng với nhân viên TGDD', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(822, 'Michael Đặng', 'Điện thoại tốt, dễ nhìn, mãu mà đẹp, pin dùng dược lâu, lưu danh bạ 1000 đc nhiều, nhân viên TGDD & ĐMX nhiêt tình, hỗ trợ KH rất tốt.', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(823, 'Luong anh tuấn', 'Mới mua hôm qua.Rất hài lòng thái độ nhân viên tại dienmayxanh Tân Hiệp, Kiên Giang vì dễ mến, ân cần, nhã nhặn và chu đáo', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(824, 'Hoàng văn Vụ', 'Sp tốt.mới mua tưởng 350k nhưng lúc thanh toán còn 295k vì là khách hàng thân thiết.quá tuyệt vời.mặc dù máy ko hỗ trợ cỡ chữ to để dành cho người già', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(825, 'bạch cường', 'sản phẩm tốt của nokia. pin khỏe. nghe gọi rõ ràng . nhân viên giao hàng đúng giờ. nhiệt tình, vui vẻ. tôi rất hài lòng.', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(826, 'Aty', 'Sản phẩm tốt Rất hài lòng dòng nokia thì khỏi chê rồi .. Nhân Viên nhiệt tình & vui vẻ', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(827, 'Nguyễn Hoàng Minh Duy', 'Máy rất ok nhân viên vui vẻ khách hàngTôi rất hài lòng với nhân viên ở điện máy xanhTôi sẽ ủng hộ điện máy xanh dài dài', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(828, 'Dương Thị Ngọc Thoa', 'Phải nói là nokia lề dòng điện thoại này rất đc kiểu dáng cũng đẹp, giá thì hợp với mn. Mọi người nên xem xét mà mua 😁', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
(829, 'Nguyễn Kim Liên', 'Sản phẩm rất tốt. Có thể giới hạn thời gian gọi, dùng rất là thích. Ủng hộ dài dài', '', '5', 141, '2020-04-11 08:06:08', NULL, NULL),
