(1021, 'Nguyễn Minh Tiến', 'Sản phẩm chất lượng tuyệt vời, nghe gọi rất ok, có tính năng ghi âm cuộc gọi rất bổ ích. xài 3 ngày chưa sạc lần nào.', '', '5', 129, '2020-04-11 08:06:08', NULL, NULL),
(1022, 'Bảo Thịnh', 'Máy thiết kế đẹp , mọi chức năng hoàn hảo , nghe gọi loa to rõ ràng , bạn nhân viên phục vụ rất thân thiện giàu kinh nghiệm !', '', '5', 129, '2020-04-11 08:06:08', NULL, NULL),
(1023, 'võ thị ánh nguyệt', 'thiết kế nhỏ gọn ,loa lớn nghe gọi rất rõ, pin sử dụng rất bền, giá thành hợp lí máy sử dụng rất tốt', '', '4', 129, '2020-04-11 08:06:08', NULL, NULL),
(1033, 'Phan Xuân Lộc', 'Điện thoại xài êm, ít hao pin, nút bấm êm tay, loa lớn âm thanh rất chuẩn, thích hợp cho những người lớn tuổi sử dụng, rất ưng ý khi mua sản phẩm tại điện máy xanh', '', '5', 129, '2020-04-11 08:06:08', NULL, NULL),
(1034, 'tô hoài toàn', 'sản phẩm thiết kế nhở gọn, pin bền, chế độ bảo hành tốt, nhân viên phục vụ nhiệt tình, rất hài lòng', '', '5', 129, '2020-04-11 08:06:08', NULL, NULL),
(1035, 'Quang Khải', 'sản phẩm tốt, có ghi âm cuộc gọi tự động, loa to sóng khỏe phù hớp với mọi người sử dụng, chỉ tiếc nhân viên trang trí lắp sim chặt quá, tháo sim ra cũng khó. tôi cho 4 sao', '', '4', 129, '2020-04-11 08:06:08', NULL, NULL),
(1036, 'Thảo', 'Thiết kế nhỏ gọn, dễ bỏ túi. Pin dùng được lâu và phím dễ bấm. Rất ưng ý về sản phẩm', '', '5', 129, '2020-04-11 08:06:08', NULL, NULL),
(1037, 'Lương minh tuấn', 'thích dòng đt cơ bản nokia , khoái nhất cài tin nhắn nhạc chuông có lời. Dễ sử dụng', '', '5', 129, '2020-04-11 08:06:08', NULL, NULL),
(1038, 'M luan', 'Máy ngon,  bắt fm rất mạnh. Loa to rõ,  điểm trừ là cái màn hơi bé và danh bạ khó copy', '', '5', 129, '2020-04-11 08:06:08', NULL, NULL),
(1039, 'Ngân', 'Thiết kế đơn giản hiện đại. Nhỏ gọn dễ bỏ túi. Loa lớn pin sử dụng lâu. Chất lượng sản phẩm rất tốt', '', '5', 129, '2020-04-11 08:06:08', NULL, NULL),
