(1202, 'anh minh', 'android 8.1 là tốt rồi độ bền cao giá tối lên mạng nhanh hôm trước máy dính nước mà vẫn sống hàng chất lượng cao ghê luôn quá tuyệt vời nên mua chống trộm cao luôn mạt khẩu sai khoá máy mot ngay', '', '4', 117, '2020-04-11 08:06:08', NULL, NULL),
(1203, 'Pin', 'Máy đẹp, camera chụp cũng bình thường nhưng vs giá dưới 2tr mà có camera kép là một điểm cộng. Với lại có  Android 8 hiếm máy nào dưới 2tr mà có dc', '', '4', 117, '2020-04-11 08:06:08', NULL, NULL),
(1204, 'Đức vinh', 'Cân nhắc trước khi mua máy lắc lắm . Hay bị văng ra khỏi ứng dụng . Cảm ứng 2diem . Nên mua s60 vì nó nhỉnhh hơn nhiều so với con s61', '', '1', 115, '2020-04-11 08:06:08', NULL, NULL),
(1205, 'Nguyễn Minh Trí', 'Gọi thì k gọi đc, truy cập 3G cũng tệ. Mà có gọi thì người ở đầu kia cũng chẳng nghe thấy gì.', '', '1', 115, '2020-04-11 08:06:08', NULL, NULL),
(1206, 'Trung LeTan', 'Điện thoại tệ nhất mình từng mua, giật lag tung chảo. , mặc dù mình không hề cài ứng dụng gì cả', '', '1', 115, '2020-04-11 08:06:08', NULL, NULL),
(1207, 'Le binh khang', 'Mới xài 2 ngày Ok coi YouTube rất mượt mà , lượt Facebook và zalo nhanh, chụp hình qua xuất sắc', '', '5', 115, '2020-04-11 08:06:08', NULL, NULL),
(1208, 'Nguyễn  thị  thuỳ  trang', 'Máy  khá  mượt  so với  tầm  giá  vậy  OK rồi  gọi  videos call hình  ảnh  rỏ  âm  thanh  khá  ổn  , máy  đẹp  hợp  thời  trang  nữ  tính  , OK.', '', '3', 115, '2020-04-11 08:06:08', NULL, NULL),
(1209, 'Phạm văn kiên', 'Ko giống như trong ảnh mô tả, cảm biến vân tay ko giống như màu máy. 1 sao là đủ', NULL, '1', 115, '2020-04-11 08:06:08', NULL, NULL),
(1210, 'nguyen phuc nguyen', 'nó khá tốt về giá, chơi game nhẹ thì cũng muotj nhưng game nặng thì ko chỉnh mã setting được', '', '5', 115, '2020-04-11 08:06:08', NULL, NULL),
(1211, 'đinh minh bảy', 'máy xài rất bền và ổn định jphone samsung quên đi.. mua tiền ít ngon hon tiền nhiều', '', '5', 115, '2020-04-11 08:06:08', NULL, NULL),
(1212, 'Duy pro', 'Máy này cũng tạm ổn.tiền ít đừng đòi hít hàng thơm.máy hơi bị oke .có vân chân.có 2 camera chụp cũng ổn', NULL, '2', 115, '2020-04-11 08:06:08', NULL, NULL),
(1213, 'Dương Tấn Hiếu', 'Máy rất tốt.Trải nghiệm Liên quân rất ổn đi khi bật đồ hoạ thấp+fix lag.Ram 16GB không lo đầy bộ nhớ.Màng hình HD+ rất OK.Máy khá tốt số với tầm giá!!!!', NULL, '5', 115, '2020-04-11 08:06:08', NULL, NULL),
(1214, 'Kiara Uri', 'Điện thoại tầm giá như này là quá ok rồi ạ , chơi được những dạng game nhẹ + fb , zalo , mess ,... Lâu lâu vẫn có hiện tượng giật lag nhẹ nhưng học sinh xài thì cực ok nha :))', '', '5', 115, '2020-04-11 08:06:08', NULL, NULL),
(1215, 'Lê Bình Khang', 'Mới xài 2 ngày cấu hình tốt , chụp hình xóa phông tốt ; cảm biến vân tay nhạy, chơi Facebook rất nhanh', '', '5', 115, '2020-04-11 08:06:08', NULL, NULL),
(1216, 'Ngô Minh Tâm', 'Khá ổn so với những máy cùg tầm giá Mẫu máy thiết kế đẹp chất lượng tốt hf ảnh chuẩn giống với quảng cáo 5* nhà', '', '5', 115, '2020-04-11 08:06:08', NULL, NULL),
(1217, 'Tú', 'Đúng là phải nói là rất tuyệt Xem phim lướt web thoải mái không bị lag Nói chung là cái gì cũng được', '', '5', 115, '2020-04-11 08:06:08', NULL, NULL),
(1218, 'Phuong khanh linh', 'Mấy này đẹp thì đẹp sang thất thiệt kể điểm 10 màn hình tốt chụp hình k nét làm còn hỏi mờ anh nhiều hát điểm 5 chíp tệ qua cầu hình qua thấp so với già sém yotube màn hình có lại như tí lệ 16:9 không rãn hết màn hình rõ ràng mạnh hình to lại sém mạnh hình bé tức cả mắt nhà sản xuất làm màn hình trận viên làm gì giả tạo lừa rối cho không không thèm lấy chỉ được đẹp mã', '', '1', 115, '2020-04-11 08:06:08', NULL, NULL),
(1219, 'Lan', 'Sản phầm dùng ổn , với giá thành này thì thi thoảng chậm và lag là điều bình thường.', '', '4', 113, '2020-04-11 08:06:08', NULL, NULL),
(1220, 'Lê thị kim loan', 'Gía chỉ 1.390.000 mà có vân tay, nhận diện khuôn mặt.. nhắn tin không cần bấm..nói vào micro điện thoại chữ tự nổi lên bấm gửi là xong..camera sau kép 13mp nữa ..chụp ảnh rất đẹp..pin trâu..sạc nhanh..mua cho em gái mình sài rất thích.so với giá này thì quá tuyêt vời.😊😊😊', NULL, '5', 113, '2020-04-11 08:06:08', NULL, NULL),
(1221, 'phát', 'Giá rẻ, may mắn còn được khuyến mãi ốp và kính cường lực, và giảm giá do khách hàng thân thiết nữa. Máy vừa đủ sài, camera sau đẹp hơn trước. Pin rất tốt. Cảm biến vân tay ổn. Nhận diện khuôn mặt chậm. Màn hình sáng hd+ mà rõ màu tươi. Chỉ có máy xử lý chậm thôi.', '', '4', 113, '2020-04-11 08:06:08', NULL, NULL),
(1222, 'Lê Hoàn', 'Với giá máy mới 1.390.000 vậy là được rồi không đòi hỏi gì thêm nữa . Xem phim , gọi zalo , nghe nhạc , pin 3000 quá ổn. Mẫu mã thời trang, rất phù hợp cho người thu nhập thấp.', '', '5', 113, '2020-04-11 08:06:08', NULL, NULL),
(1223, 'Nguyễn Minh Hiếu', 'Máy chạy ổn, chụp ảnh rõ, bình thường chơi zalo với cả fb, em không chơi game cho nên bây giờ thấy vẫn chạy tốt, chưa có lỗi gì', '', '5', 113, '2020-04-11 08:06:08', NULL, NULL),
(1224, 'Lưu thủy', 'Khá ổn trong tầm giá . Điểm duy nhất k tốt là vân tay k nhạy và mình k thích vỏ ốp cứng', '', '4', 113, '2020-04-11 08:06:08', NULL, NULL),
(1225, 'Văn Rở', 'Máy đẹp, dễ sử dụng nhưng thời gian rung chuông khi có ai gọi đến rất ngắn, t chưa kịp bắt máy là máy đã tự tắt rồi. Đã thử lại nhiều lần nhưng vẫn thấy như vậy, có cách nào khắc phục không mn.', '', '4', 114, '2020-04-11 08:06:08', NULL, NULL),
(1226, 'Hùng Khanh', 'Sử dụng 230 đã nhiều máy rồi , mới mua cái mới 14 - 2 , máy ok , tiếc ko có danh sách đen như 216 , 3310', '', '5', 114, '2020-04-11 08:06:08', NULL, NULL),
(1227, 'Triều Nguyễn', 'Giống hàng mã, Nokia giờ chỉ còn là cái tên, sử dụng chưa được năm đã vô bảo hành', '', '2', 114, '2020-04-11 08:06:08', NULL, NULL),
(1228, 'phát', 'Mua về sử dụng chưa được 1 tuần đã đổi 1 cái mới do lỗi NXS thật là tệ nhưng mà được cái nhân viên vui vẻ nhiệt tình', '', '1', 114, '2020-04-11 08:06:08', NULL, NULL),
(1229, 'Phú', 'Máy ọp à ọp ẹp dùng không được chắc chắn quá tội máy tàu. Nokia cứ đà này mọi người quay lưng lại  cái tên  Nokia không còn ai biết đến nữa cả. Sản suất cục gạch còn không lên hồn. Tưởng làm được cái Nokia 800 toug mà dùng chả ra sao hệ điều hành thì (THIểu NĂNG TRÍ Tuệ) thua hết 1280 thế mà giờ không sản suất trở lại,cằng sáng tạo lại cằng lỗi, thụt lùi.', '', '1', 114, '2020-04-11 08:06:08', NULL, NULL),
(1230, 'Nguyễn Hải Đăng', 'Bảo hành không có tâm chút nào., mấy lỗi các kiểu, mua cho bà già sài được 20 ngày bị mất nguồn sạc không nên,ra bảo hành lại nói bị ô xi hoá lỗi của ngươi dùng,báo giá để sửa... bảo hành có tâm chút đi ,cái đt cũng hơn triệu bạc thôi mà làm ăn như ... nếu mà lỗi của người dùng thì k bh đem bảo hành', '', '1', 114, '2020-04-11 08:06:08', NULL, NULL),
(1231, 'Vương Thịnh', 'Giao hàng nhanh , sản phẩm đẹp, nhân viên tư vấn nhiệt tình rất hài lòng,âm thanh sản phẩm tốt thích hợp cho người già', '', '5', 114, '2020-04-11 08:06:08', NULL, NULL),
