(2042, 'Trân', 'Đt xài ok nha .pin sạc nhanh xài khá lâu . Cam thì ko xài nhiều nên ko đánh giá đc. Máy chạy mượt dễ xài. Bắt sóng ok nha', '', '5', 88, '2020-04-11 08:06:08', NULL, NULL),
(2172, 'Nguyễn Tâm', 'Mình mowia mua sài đc vài tiếng. Vì đặt onl nên giá giảm còn 2.790.000. pin trâu, chơi game mượt, camera thì k đc đẹp lắm. Mọi thứ đều ổn trong tầm giá nha', '', '4', 83, '2020-04-11 08:06:08', NULL, NULL),
(2173, 'Lâm', 'Hôm 20 tháng 10 có mua một máy bản màu kim cương hồng giá 2390k với giá đó thì phải nói là tuyệt vời. Vì các hãng khác không có máy nào được như vậy cả. Vivo co y91c, xiaomi co redmi 7a,huawei có y7 pro... Đều có giá cao hơn nhưng lại thua realme 3 về mọi mặt. Từ cấu hình, camera,ram bộ nhớ trong...sài đã hơn ba tháng mà vẫn ổn định và muợt mà. Pin lại rất lâu hết. Nói tóm lại với giá 2390k thì quá hài lòng', '', '5', 83, '2020-04-11 08:06:08', NULL, NULL),
(2174, 'Thạch Sa  Giang', 'Mình mua và sài được 9 tháng chơi game vẫn rất êm,  pin trâu,  mượt nữa nói chung rất ổn,  e rất thích sản phẩm này', '', '5', 83, '2020-04-11 08:06:08', NULL, NULL),
(2175, 'Đức son', 'Mua loại này đáng đồng tiền.camera 13mp mà nét ngang với 48mp.loa to.nghe nhạc chất lượng cực hay.điểm trừ khi ra nắng màn hình bị chói nhợt và mặt sau của máy hơi xấu', '', '4', 83, '2020-04-11 08:06:08', NULL, NULL),
(2176, 'Nam', 'Nếu sử dụng một lúc 3 ứng dụng thi một trong ba ứng dụng sẽ bị khởi động lại .nên ae chạy xe công nghệ lưu ý khi chọn mua máy này . mình chạy grab mà lúc nào cũng phải kiểm tra app sợ bị mất kết nối', '', '4', 83, '2020-04-11 08:06:08', NULL, NULL),
(2177, 'châu phú tặng', 'tôi rất thích điện thoại này pin rất trâu mà cấu hình đẹp nữa và thái độ phục vụ của nhân viên rất chu đáo với khách hàng.tôi mong nhà sản xuất phát triển ra nhiều điện thoại tốt bền và pin trâu hơn để đáp ứng cho người tiêu dùng.', '', '5', 83, '2020-04-11 08:06:08', NULL, NULL),
(2178, 'Kim Thạnh', 'Mua ở dienmayxanh định quán đồng naiChị nhân viên rất nhiệt tình và dễ thương nữaDùng được 6 tháng rồi, pin cực trâu luôn nha, vì mình chỉ quanh quẩn fb với YouTube nên không tốn pin nhiều có khi 2,3ngày mới sạc 1 lần, camera chụp cực nét luôn, sài điện thoại này tự nhiên thấy bản thân trưởng thành ghê, thích chụp cam mộc và đăng ảnh lên f cũng mộc, vì không chơi game nên không thể nhận xét về gameI love realme', '', '5', 83, '2020-04-11 08:06:08', NULL, NULL),
(2179, 'Lê Uyển', 'Nhân viên rất thân thiện và tư vấn nhiệt tình ạ Mới mua hôm qua sử dụng cũng ok ạ', '', '5', 83, '2020-04-11 08:06:08', NULL, NULL),
(2180, 'Nguyễn Khanh', 'Tất cả đều tốt. Pin trâu, loa tốt camera chấp nhận ở mức giá. Điểm trừ ram 3G nhưng đa nhiệm cực kì yếu. Vote 4*', '', '4', 83, '2020-04-11 08:06:08', NULL, NULL),
(2181, 'Ly', 'Rất ổn camera hơi kém rõ nét được trong tầm giá này nhân viên rất tốt nhiệt tình giúp đỡ', '', '5', 83, '2020-04-11 08:06:08', NULL, NULL),
(2182, 'Lương sơn', 'Rất phù hợp cho sinh viên chỉ vs giá trên 3tr. Pubg mượt. Liên quân fps 60CPU khá ổn định', '', '5', 83, '2020-04-11 08:06:08', NULL, NULL),
(2183, 'Kim Trinh', 'Camera hơi tệ mọi thứ ok nhé aeNv nhiệt tình phục vụ tốt Xứng đáng danh hiệu realmelYes', '', '4', 83, '2020-04-11 08:06:08', NULL, NULL),
(2184, 'Manh van', 'Mới mua về thì ok nhưng dc khoảng 2 đến 3 tháng thì ko chấp nhận dc wjfj căng đét 4g sóng khỏe mà gọi điện video hay chơi game thì giật tung máy mức pin 4230 mà hao quá nhanh Khuyên anh ae mua chơi game nên mua chíp snap đừng mua helio ko mượt đâu', '', '1', 83, '2020-04-11 08:06:08', NULL, NULL),
(2185, 'Yđai.ct', 'noí chung mọi thứ đều ổn trừ loa ngoài qúa tệ tế từng thấy hãng điện thoại nào loa ngoài tệ như vậy bát rè mở2/3âm lượng rè không nghe nổi nhạc luân', '', '3', 83, '2020-04-11 08:06:08', NULL, NULL),
(2186, 'Ngọc', 'Dùng thì k nóng máy lắm...nhưg dù sạc đúng sạc realme máy vẫn nóng quá....mong phản hồi 😣Còn lại ok', '', '3', 83, '2020-04-11 08:06:08', NULL, NULL),
(2187, 'Nguyễn Hoài Vũ', 'Nhân viên phục vụ nhiệt tình. Sản phẩm tốt trong tầm giá. Nói chung rất tuyệt vời.', '', '5', 83, '2020-04-11 08:06:08', NULL, NULL),
