(3573, 'Lê Thị Xuân Bích', 'mua được hơn 1 tháng, mình sử dụng chủ yếu là zalo, facebook và có chơi liên quân thấy pin xài khá lâu, một ván game tầm 4% pin . Vân tay và nhận diện khuôn mặt không nhạy lắm, hơi chậm . Bàn phím lúc mới sử dụng thì ko được mượt, sài một thời gian thì đã đỡ hơn, zalo khi nhắn tin đến thì nó hơi lag và gọi video call zalo lâu quá máy tự khởi động lại. Nói chung tùy theo nhu cầu và theo mình thì máy sài được ko quá tệ như mọi người đánh giá , tuy nhiên khi mua sản phẩm thì mình cảm thấy nhân viên như ép khách mua sản phẩm vậy, mới đầu mình tính mua máy A30s nhưng chị nhân viên lại nói máy ko có tai nghe và sách hướng dẫn sử dụng.', '', '4', 4, '2020-04-11 08:06:08', NULL, NULL),
(3574, 'Linh', '8tr thì hơi cao còn 6tr5 mọi thứ ok.game khôg quá tốt nhưg chụp ảnh đẹp.......................', '', '5', 4, '2020-04-11 08:06:08', NULL, NULL),
(3575, 'Nguyễn Hoa', 'Mua dùng được 1 tháng, chơi game ping liên tục nghĩ mà chán. Khuyên ae nào chơi game đừng mua con này ức chế lắm.', '', '1', 4, '2020-04-11 08:06:08', NULL, NULL),
(3576, 'Ta tuệ', 'Tuyệt vời máy chay rất mượt đặc biệt là camera chụp rất nét ngay cả chụp vào ban đêm', '', '5', 4, '2020-04-11 08:06:08', NULL, NULL),
(3577, 'Thanhtung', 'Mới dùng được 3 ngày nhìn chung máy chạy mượt.hình ảnh đẹp.Màn hình lớn nhưng cầm rất vừa tay.vân tay nhậy....5 sao cho samsung a51', '', '5', 4, '2020-04-11 08:06:08', NULL, NULL),
(3578, 'Nguyen van tai', 'Mới mua sài cũng thấy ổn.nhân viên DMX rất nhiệt tình và thân thiện.vẫn sẽ còn ủng hộ DMX', '', '5', 4, '2020-04-11 08:06:08', NULL, NULL);
