(8172, 'Mã thế doanh', 'Hơi làng xíu ... Nhưng tầm giá này k đòi hỏi gì thêm nữa Hình như face id có chỉ để cho đủ thôi . K dùng đk', '', '5', 121, '2020-04-22 15:25:34', NULL, NULL),
(8173, 'Đông', 'Mới mua em nó dùng nghe gọi linh tinh.1 triệu thì thế này là cùng.dùng máy còn cơ bản này thì bấm từ từ chứ nhanh quá nó phản xạ không kịp...', '', '5', 121, '2020-04-22 15:25:34', NULL, NULL),
(8174, 'Quách thống', 'Hôm qua mua về mới nhận ra máy chỉ để nghe gọi mà thôi không dùng được ứng dụng.', '', '2', 121, '2020-04-22 15:25:34', NULL, NULL),
(8175, 'Phuc', 'Máy xài ổn trong tầm giá .Mặc dù hơi lag tí nhưng giá vậy là ok với một chiếc máy tầm trung rồi .', '', '4', 121, '2020-04-22 15:25:34', NULL, NULL),
(8176, 'Truyện', 'Máy lag, không nhúc nhích được, TGDD thì không cho đổi trả. Đã mua rất nhiều ở TGDD và đây là lần cuối cùng. Thề tẩy chay TGDD', '', '1', 121, '2020-04-22 15:25:34', NULL, NULL),
(8177, 'Trương Thoại', 'Tạm với giá. Máy chậm và lag. Mua về cho người già nhu cầu ít thì ổn. Pin nhanh hết. Máy nhẹ.', '', '2', 121, '2020-04-22 15:25:34', NULL, NULL),
(8178, 'Tuyên', 'Hi mới mua được 1 ngày nhưng cũng thấy khá ok . Những dùng lâu dài thì không biết như thế nào', '', '5', 121, '2020-04-22 15:25:34', NULL, NULL),
(8179, 'Hoàng', 'Máy tất chậm và lag lắm. Mua máy cũ có khi ngon hơn,vs lại khi xem phim hay luớt wed âm thanh vs hình ảnh kon đồng bộ.tệ lắm :(((', '', '2', 121, '2020-04-22 15:25:34', NULL, NULL),
(8180, 'duy', 'sử dụng hơi lag, phiền phức ở chổ cái ứng dụng premision control tắt chưa được, chưa được 1 tháng đã bị chảy màn hình', '', '1', 121, '2020-04-22 15:25:34', NULL, NULL),
(8181, 'Thanh', 'Với giá máy mới 1.390.000 vậy là được rồi không đòi hỏi gì thêm nữa . Xem phim , gọi zalo , nghe nhạc , pin cũng ổn mà', '', '5', 121, '2020-04-22 15:25:34', NULL, NULL),
(8182, 'han', 'sản phẩm tuyệt vời so với giá. có chút hoài cổ của 3310 thời xưa. pin cũng khỏe, dùng 5 đến 6 ngày là ok', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8183, 'Bùi Bình', 'Sản phẩm mỏng gọn nhẹ mới mua nhân viên tgdđ Thạnh Mỹ Đơn Dương vui vẻ nhiệt tình', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8184, 'The Anh Pham', 'máy đẹp, nhỏ gọn trong lòng bàn tay.chữ to và rõ ràng.', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8185, 'Đỗ Duy Thiên', 'Nhân viên nhiệt tình , lễ phép , máy nhỏ gọn đẹp , pin dùng liên tục 5 -6 ngày mới phải sạc , loa tốt  . Điểm duy nhất chưa hài lòng là độ rung của máy .', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8186, 'phạm văn tư', 'mấy dùng dk nửa năm thì khe thẻ nhớ hỏng Z. pin hao . máy nóng đột ngột. khe thẻ nhớ thì hỏng . ko dk như quảng cáo.', '', '1', 119, '2020-04-22 15:25:34', NULL, NULL),
(8187, 'Văn Tiến', 'Máy nghe gọi tốt, đẹp, nhỏ gọn cầm thích tay, máy mình mua đã dùng hơn 1 năm, hết bảo hành. Hiện mới bị lỗi phím gọi (bị liệt).. lỗi không phải do rơi hay bị nước vào mà là đang dùng binh thường thì bị.. cho mình hỏi thế giới di động có sửa được lỗi này không? Xin cám ơn.', '', '4', 119, '2020-04-22 15:25:34', NULL, NULL),
(8188, 'Minh Tuấn', 'Gì cũng tốt -màn hình đẹp - có vỏ máy cầm ko khít - ộp ẹp phải mua thêm ốp lưng.', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8189, 'Lương Minh Tuấn', 'nokia 3310 xài ổn - phím khó tróc - nhà máy VN ráp máy ẩu - khung ống kính có dấu móng tay mở ra làm tỳ dấu', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8190, 'Kiệt', 'Máy mới xái hơn 1 tháng mà bụi đã vào màn hình rồi, biết vậy không mua, phím dễ trầy', '', '1', 119, '2020-04-22 15:25:34', NULL, NULL),
(8191, 'Thắng', 'May sư dung gọn và tốt ,hiển thị khá ok', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8201, 'DƯƠNG THỊ LINH ĐAM', 'Sản phẩm tốt, đa dạng màu sắc, chính sách bảo hành an tâm, pin sử dụng khá lâu, âm thanh nghe rõ , êm', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8202, 'Lưu thị thanh tuyền', 'Điện thoại nhỏ gọn màu sắc dễ thương. Nhân viên vui vẻ nhiệt tình, phục vụ mọi mặc đều rất hài lòng.', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8203, 'Nguyễn Thị Thủy', 'tốt rất dùng sản phẩm của điện máy xanh , máy dùng nghe gọi rất to, tôi rất thích và sẽ quay lại ủng hộ', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8204, 'Thanh Tùng', 'Tuyệt vời ông mặt trời cho hoài niệm về nokia thời xưa cũThiết kế hiện đại sang chảnhQuá yêu thương', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8205, 'ANH SỸ', 'Chưa bao giờ thất vọng về sản phẩm của nokia, máy thiết kế đẹp, âm thanh trong hay, pin sàI khỏe, máy nhỏ gọn', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8206, 'Phạm thị nhung', 'Điên thoại chụp ảnh rất đẹp. Màn hình sáng. Giá hấp dẫ vì đã giảm so với trước rất nhiều. Nhỏ gọn tiện bỏ túi. Pin dùng lâu. Nói chung rất ưng ạ:)))', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8207, 'Nguyễn thị ngân', 'Máy nhỏ gọn, kiểu dáng và màu sắc bắt mắt. Pin xài lâu hết. Nghe gọi tốt, chính sách bảo hành oki. Lại đc nhân viên tư vấn nhiệt tình chu đáo nữa...', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8208, 'Yến', 'Máy nhỏ gọn, thiết kế đẹp, pin ok, dùng cai nghiện smartphone rất tốt. Nhân viên tư vấn rất nhiệt tình, dễ thương', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8209, 'Anh Nghiệm', 'thiết kế sản phẩm thuận tiện cho người sử dụng, máy cầm pin và nghe gọi rất rất tốt', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8210, 'Huỳnh Giao', 'Sản phẩm đẹp, hàng chính hãng . Chất lượng Ok. Nhân viên chu đáo nhiệt tình . Chính sách bảo hành Rất Ok', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8211, 'NGUYỄN ÁI LEN', 'Âm thanh rất rõ. Kiểu dáng và màu sắc đẹp. Có hỗ trợ 2 sim và thẻ nhớ luôn. Pin xàI lâu hết. Sản phẩm xàI bền lắm.', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8212, 'Được Lê', 'Điện thoại có ngoại hình đẹp. Đặt biệt là có cả 2 sim. Pin dùng rất lâu. Giá còn hơi cao. Giảm tí nữa là tuyệt', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8213, 'Trần Văn Tuấn', 'sản phẩm mua rất tốt tôi rất thích mua sản phẩm bên điện máy xanh và thế giới di động và cũng sau này. sảm phẩm thuận tiện cho công việc của tôi .tôi rất thích tính năng và mới Thiết kế gọn đẹp. Dễ cầm. Giá hợp thị trườh có bảo hạn sản phẩm chất lương. Pin xài lâu. Phím dễ bấm', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8214, 'đinh thị hương', 'sản phẩm tốt kiểu dáng đẹp dễ sử dụng nhân viên tư vấn rất nhiệt tình mình rất là hài lòng', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8215, 'Anh phương', 'Thiết kế gọn đẹp. Dễ cầm. Giá hợp thị trườh có bảo hạn sản phẩm chất lương. Pin xài lâu. Phím dễ bấm', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8216, 'Hằng', 'Điện thoại nhỏ gọn,pin khóe,sóng tốt.Với mức giá này mà chất lượng tốt như vậy là rất OK', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8217, 'Nguyễn Trọng Phú', 'sản phẩm đẹp, nhỏ gọn, chức năng phù hợp người dùng, màu sắc đa dạng. thiết kế thanh lịch , thời trang', '', '4', 119, '2020-04-22 15:25:34', NULL, NULL),
(8218, 'SỚm', 'nhiều màu cho mình lựa chọn rất đẹp và trẻ trung, nhỏ gọn, pin lâu,loa lớn dễ nghe,  rất thích', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8219, 'Luân', 'Máy nghe rất ổn, nhỏ gọn, lâu hết pin, so với các máy cùng loại và cùng tầm giá tốt hơn', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8220, 'Trọng Mãi', 'Máy gọn nhẹ. Đẹp thiết kế khỏi chê. Màu sắc bắt mắt. Âm thanh trong chup hinh đẹp', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8221, 'Trần Vũ Phước Toàn', 'Thiết kế nhỏ gọn, mẫu mã đẹp, phím trơn không phai màu, giá phù hợp, sử dụng hơn 1 năm vẫn cầm pin, hài lòng với sản phẩm này', '', '5', 119, '2020-04-22 15:25:34', NULL, NULL),
(8222, 'vodat', 'mới mua hôm qua,hôm nay đổi lại máy mới do nắp lưng vênh,máy nghe gọi tốt,nhân viên nhiệt tình.thank you.', '', '4', 114, '2020-04-22 15:25:34', NULL, NULL),
(8223, 'hoàng đàn', 'máy pin tốt, màn hình to, thời gian đàm thoại dài, thiết kế sang trọng nhìn rất hiện đại, đặc biệt loa to và mic rõ ràng,hành trình phím tốt', '', '5', 114, '2020-04-22 15:25:34', NULL, NULL),
(8226, 'Xuân Trường', 'Điện thoại bị lỗi á admin, cam ko chụp dk sau một thgian sử dụng màn hình cam bị đen như hình.', NULL, '3', 110, '2020-04-22 15:25:34', NULL, NULL),
(8227, 'Thành Duy', 'Những điểm thấy hài lòng là: màn hình đẹp, pin ổn, cam cũng tạm trong tầm giá rẻ,.... điểm yếu là loa hơi nhỏ âm bass chưa tốt, mặt lưng bám mồ hôi, khe sim thẻ nhớ hơi kẹt phải nhẹ tay không thì rất dễ gãy( thôi tiền nào của đấy cx phải chấp nhận thôi). Mình Thấy sản phẩm vs những người già và những người chỉ lướt web xem phim chứ game chỉ chs mấy game vớ vẩn thôi.', '', '5', 110, '2020-04-22 15:25:34', NULL, NULL),
(8228, 'E Đạt', 'Mua ủng hộ hàng việt nam. Máy mua lại mà dùng đến bh thấy quá ok. Dùng đc 2 tháng r. Ko biết lâu dài thì ntn', '', '5', 110, '2020-04-22 15:25:34', NULL, NULL),
(8229, 'Phan Ngọc Trí', 'Dùng bình thường, xem phim, facebook, youtube ok nhưng khi sạc thì mau nóng lên tới 48oC.', '', '3', 110, '2020-04-22 15:25:34', NULL, NULL),
(8230, 'Huỳnh Văn Lợi', 'Ủng hộ hàng Việt nên mua. Mình dùng để chơi game với nghe nhạc thôi. Mà điện mau nóng nóng hơn cái iphone mình đang xài', NULL, '3', 110, '2020-04-22 15:25:34', NULL, NULL),
(8231, 'Mẫn', 'Sản phẩm sử dụng đối với những tác vụ cơ bản như fb mes zalo thì rất ok. Màn hình đẹp pin xài được cả ngày có khi hơn. Nhưng thua ở chỗ loa ngoài nghe nhạc rất dở. Mình mua đc hơn nửa tháng đổi 3 máy vẫn bị tình trạng loa như vậy. Nhưng thật sự rất hài lòng về khoản chăm sóc khách hàng bên tgdd đã khắc phục được tình trạng mà khách hàng gặp phải. Mình vẫn ủng hộ bên tgdd lâu dài', '', '5', 110, '2020-04-22 15:25:34', NULL, NULL),
(8232, 'Trần Thành Long', 'Máy thì cũng được cấu hình bình thường pin 3000 mAh dùng hơi bình thường chơi game vẫn ok', '', '5', 110, '2020-04-22 15:25:34', NULL, NULL),
(8233, 'Hoàng văn thương', 'Máy ổn trong tầm giá.các bạn tối ưu máy vào cài đặt ứng dụng.ko dùng cái gù chọn tắt hết đi .máy mượt nên rất nhiều.còn khoản pin thì cũng bt thôi pin 3000 ko đc so với máy đắt tiền khác.', '', '5', 110, '2020-04-22 15:25:34', NULL, NULL),
(8234, 'Vương tuyến', 'Máy sạc pin lâu. Màn hình thì đẹp. Camera không đẹp. Facebook hay bị lag phải thoát ứng dụng. Zalo thì OK. Game chỉ chơi hago thì OK. Được cái này. Pin 3000 trâu bò.', '', '3', 110, '2020-04-22 15:25:34', NULL, NULL),
(8235, 'Nguyễn thế', 'Máy thi thoảng bấm vào nút nguồn thì chuyển qua chế độ quay camera, có ai dùng bị tương tự không và chỉ giúp tôi khắc phục', '', '3', 110, '2020-04-22 15:25:34', NULL, NULL),
(8236, 'Hoàng tâm', 'Dùng cho người già cũng tạm ổnChán mỗi cái loa rè quá,thấy nhiều người kêu chắc là bị chung hết rồi', '', '4', 110, '2020-04-22 15:25:34', NULL, NULL),
(8237, 'The Power', 'Về tổng quan là chấp nhận được so với giá: Chip snapdragon 215, ram2g, rom 16g, màn hình nét độ phân giải vừa đủ, camera nhìn có vẻ lởm nhưng chụp cũng chấp nhận được, pin dùng tạm ổn vì có lúc tụt bất ngờ.', NULL, '5', 110, '2020-04-22 15:25:34', NULL, NULL),
(8238, 'Huy', 'Sản phẩm phù hợp so với giá, mọi công năng hoạt động ở mức vừa phải tốt, chỉ loa của máy âm thanh không hay và khá vỡ âm khi mở loa to, nghe nhạc k hay thôi.', '', '5', 110, '2020-04-22 15:25:34', NULL, NULL);
