(8239, 'Nguyễn Quốc Thạch', 'Điện thoại của em của em là Vsmart Star Sau 1 đêm ngủ dậy nó bị nhoè màn hình', NULL, '5', 110, '2020-04-22 15:25:34', NULL, NULL),
(8240, 'Kiên', 'mình đang dùng vinMasstel star máy rất tốt và ổn định luôn ủng hộ Vinmart .mình đùng dc khá lâu rồi', '', '5', 110, '2020-04-22 15:25:34', NULL, NULL),
