(9088, 'Lực', 'Điện thoại đẹp , mua từ mùng 4 tết đến nay thấy dùng vẫn tốt , sạc pin 2h30p dùng được đến tận 2 ngày , camera trước sau chụp đẹp dưới điều kiện ánh sáng tốt còn dưới điều kiện ánh sáng yếu như ảnh ở dưới thì dùng chế độ ban đêm ( chỉ camera sau ) khá tốt', NULL, '5', 76, '2020-04-22 15:25:34', NULL, NULL),
(9089, 'Hiếu anh', 'Máy mới mua hơn tuần.Máy thì thoảng bị đơ màn hình đôi khi không nhận vân tay....', '', '2', 76, '2020-04-22 15:25:34', NULL, NULL),
(9090, 'Duong nguyet thu', 'E mới mua dt hôm 27 tết mà điện thoại lướt chậm qua mà bàn phím bấm khó nữa chứ,vào mới ứng dụng thì bấm 2,3 lần mới vào dc và nứt tắt mở thì lâu làm mới lên,khóa bằng khuôn mặt thì đoi lâu mới báo dc thậm chí ko đung khuôn mặt của mình cùng vào dc luôn,bấm chứ này qua chứ khác', '', '1', 76, '2020-04-22 15:25:34', NULL, NULL),
(9091, 'Đoàn mai', 'Mua chưa được 2 ngày Pin tầm 3700 để qua đêm hụt 10% lâu lâu màn hình nhảy lung tung ,sạc lâu .', '', '2', 76, '2020-04-22 15:25:34', NULL, NULL),
(9092, 'Phan việt hoàng', 'Sản phầm dùng mượt rất tốt chơi game ko lag pin trâu khỏi nói khi mua nhân viên nhiệt tinh', '', '5', 76, '2020-04-22 15:25:34', NULL, NULL),
(9093, 'Mập', 'Máy khá mượt. Chơi liên quân ở mức 60fps liên tục. Chụp hình thì tạm. Pin trâu đấy. Chơi tầm 20 trận cũng được. Pubg mình chưa test thử. Màn hình lớn và dài. Ốp thì đặt shopee mới có vì dòng máy mới ra. Máy xài ngon cho ai có nhu cầu trung bình. Do mình xài ip 6s plus làm máy chính nên thấy k mạnh bằng.', '', '5', 76, '2020-04-22 15:25:34', NULL, NULL),
(9094, 'Ngoc', 'Mới mua được 3ngay sài ngày đầu tiên thì tốt làm hôm qua nay kg vô mạng được bị đứng máy kg à cảm ứng có lúc bấm ăn lúc thì bấm kg ăn', '', '3', 76, '2020-04-22 15:25:34', NULL, NULL),
(9095, 'Đức Nguyễn', 'Máy rất mượt, đẹp cực, chụp hình tốt đáng để mua, anh chị trong  tgdđ rất nhiệt tình', NULL, '5', 76, '2020-04-22 15:25:34', NULL, NULL),
(9096, 'Ngô Viết Tú', 'Khá hài lòng về máy. Pin trâu. Chơi game tốt nếu biết chỉnh. Thao tác hằng ngày khá mượt. Nghe bảo tháng 5 tới đc cập nhật Realme ui androi 10 sẽ ổn định hơn nữa.', '', '5', 76, '2020-04-22 15:25:34', NULL, NULL),
(9097, 'Vũ Văn Hưng', 'Máy dùng tạm được thôi mỗi toi ko có full HD vs sạc nhanh nên k thích lăm...màn hình to mà  ko có full HD nên ko đc nét lắm', '', '2', 76, '2020-04-22 15:25:34', NULL, NULL),
(9098, 'Phương', 'Máy em mua và dùng được hơn một tháng, theo em pin vẫn ổn nhưng phần cảm ứng đôi khi không nhạy, chưa kể dạo này còn rất hay giật lag. Nhưng không đáng kể', '', '4', 76, '2020-04-22 15:25:34', NULL, NULL),
