(9887, 'Bui van tra', 'Nhiều người nói iPhone không chịu thay đổi dáng vẻ bên ngoài như các hãng khác như mình thấy thì các hãng khác trừ Sony ra thì còn các hãng còn lại hầu như cũng có đời máy na ná về phần mặt sai của iPhone đó thui', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9888, 'Ngô Văn Khởi', 'Xài tới tháng thứ 5 thì khi sạc phát hiện sạc chập chờn, còn lại tất cả đều rất tốt', '', '3', 33, '2020-04-22 15:25:34', NULL, NULL),
(9889, 'Phạm Quốc Huy', 'tôi dùng được hơn 2 năm rồi. sản phẩm chạy mượt. tuy nhiên do thời gian nên pin có kém đi. nhưng để qua đêm pin vẫn k bị hao dù cập nhật ios 12.1.4. Trong 2 năm mỗi ngày dùng 10 tiếng cho game mà vẫn mượt. tuyệt vời. không muốn chuyển qua androi chút nào', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9890, 'Trà', 'iPhone 7 32g:nếu cột sóng còn 1 vạch các bạn dùng cách này cột sóng sẽ nhảy lên 1 vạch. Nếu các bạn dùng ốp lưng thì khoét 1 hoặc 4 lỗ ở dải angten của 4 góc cạnh.còn nếu khoét 1 lỗ thì khoét ở cạnh nút gạt tắt chuông tức là góc trên bên trái màn hình.nếu không được ace cũng đừng trách nhé.tại vì mình chỉ thử trên iPhone của mình thui', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9891, 'Trần quang việt', 'Hàng tgdd thì chất lượng tốt nhân viên nhiệt tình nói chung là đáng đồng tiền bát gạo', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9892, 'Nhu Y Nguyen', 'Sản phẩm dùng tốt, pin khoảng 5h dùng liên tục.Dùng 1 tháng chưa thấy lỗi gì. (ios 12.0).', '', '3', 33, '2020-04-22 15:25:34', NULL, NULL),
(9893, 'khải', 'Máy sài rất mượt, cấu hình chạy nhanh, thiết kế nhỏ gọn rất thuận tiện, máy đẹp, nhân viện nhiệt tình.', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9894, 'Phạm mạnh hùng', 'Với tôi, iPhone 7-32G là đủ xài . Chất lượng tốt, nhanh , mượt mà . Nếu đã xài nó thì khi chuyển qua dùng dòng khác có cùng giá thành sẽ rất bực bội ! Nói chung iPhone 7 là tuyệt !', '', '4', 33, '2020-04-22 15:25:34', NULL, NULL),
(9895, 'Huỳnh thịn mỹ hà', 'Máy xài rất ok, vừa tay cầm. Nhân viên nhiệt tình dễ thương. Cho mình hỏi trong hộp ip7 có cổng logeng kg.', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9896, 'Mr Văn', 'Chất lượng kết nối LTE quá tệ, ko bằng cái máy phụ 5s. Pin ngày sạc 2 lần. Quay video thiếu sáng quá dở. Không cài đc hack game như máy samsung.', '', '1', 33, '2020-04-22 15:25:34', NULL, NULL),
(9897, 'Phúc vinh', 'Sản phẩm nhỏ gọn vân tay mượt chơi game ko lắc rất ok nhân viên hỗ trợ nhiệt tình', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9898, 'thu thao', 'điện thoai mua ở điên máy xanh rất tin tưởng là sản phẩm chúh hãng, mình mua lâu rồi nhưng sử dụng rất hài lòng, được bảo hành toàn quốc, nhân viên nhiệt tình. rất hài lòng', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9899, 'Tràn Hoàng Kha', 'sản phẩm sài tốt,nhân viên phục vụ nhiệt tình, rất ưng ý với sản phẩm mua ở điện máy xanh', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9900, 'Hoàng Hải', 'Máy thiết kế đẹp dễ cầm nắm ,loa nghe rất ấm.chơi game k bị nóng. Pin thì xài trung bình cộng.máy mượt . Rất đáng mua!! Nv nhiệt tình chu đáo...chính sách BH tốt', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9901, 'Trần thị phương quanh', 'Thiết kế sang trọng,kiểu dáng gọn,đẹp,điện thoại mượt mà,xài rất êm,nhân viên phục vụ nhiệt tình,vui vẻ. Noi chung e rất hài lòng về sản phẩm', '', '4', 33, '2020-04-22 15:25:34', NULL, NULL),
(9902, 'Diệu', 'điện thoại thiết kế đẹp mắt,dùng hay chơi game rất mượt.Pin dùng 1 ngày mới hết. lần sau có mua sẽ ủng hộ các bạn', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9903, 'La Thị Ngân', 'thật sự quá chất lượng , mượt mà tốc độ xuất sắc ok yeah , nút home tuyệt vời quá nhân viên nhiệt tình phục vụ', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9904, 'Mai Thị Kim Thanh', 'Điện thoại xài tốt, màn hình hiển thị sắc nét, camera chụp hình tốt, không bị mờ hay vỡ hình.', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9905, 'Diệu', 'Rất hài lòng về cách phục vụ và tư vấn về sản phẩm, sản phẩm tốt, sử dụng rất tốt, tuyệt vời', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9906, 'Nguyễn Huy Hoàng', 'Ngon lành cành đào , thật sự quá chất lượng , mượt mà tốc độ xuất sắc ok yeah , nút home tuyệt vời quá đã tê tái haha', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9907, 'nguyễn duy khánh', 'Rất hài lòng về cách phục vụ và tư vấn về sản phẩm, sản phẩm tốt, sử dụng rất ok', '', '4', 33, '2020-04-22 15:25:34', NULL, NULL),
(9908, 'Đỗ Chí Cao', 'Điện thoại lướt rất là mượt ạ ! camera chụp rất là nét .thiết kế nút home bấm rất là đã tay . Sử dụng trong công việc hay chơi game điều làm mình rất hài lòng .', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9909, 'Nguyễn Thị Ngọc Thêm', 'Thiết kế nhỏ gọn, sang trọng, camera chuẩn nét, đặc biệt cảm ứng rất mượt chế độ bảo hành lại tốt. Mình đang sử dụng rất thích. Quá tuyệt vời!!! TÔI RẤT HÀI LÒNG....', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9910, 'Trần thị Kiều Phượng', 'Sản phẩm nhỏ gọn, vừa tay thiết kế màu sắc đẹp mắt , sang trọng. Máy dùng rất mượt mà, chế độ bảo hành rất tốt, pin khoẻ .Cảm ứng mượt mà, phù hợp với công việc của mình bởi camera sắc nét. Sẽ luôn ủng hộ điện máy xanh', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9911, 'Hương', 'Sản phẩm rất đẹp , thiết kế nhỏ gọn ,cảm ứng rất tốt hinhg ảnh đẹp âm thanh nghe rất hay, chụp ảnh rất thực.', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9912, 'Hương', 'Sản phẩm sử dụng rất ok , mẫu mã đẹp nhỏ gọn pin xài lâu hết pin, cảm ứng nhậy mượt tôi rất ưng ý sản phẩm', '', '5', 33, '2020-04-22 15:25:34', NULL, NULL),
(9922, 'Trung Hậu', 'Cấu hình ko mượt đúng mức giá , chơi liên quân cũng bị giật , sử dụng ko thoait mái . nhận diện vân tay quá chậm , nhận diện khuôn mặt cũng rất chậm , phải đặt ngay mặt , thiếu ánh sáng không nhận diện .', '', '2', 32, '2020-04-22 15:25:34', NULL, NULL),
(9923, 'Liêm nguyễn', 'A71 mình sài rất ok  pin trâu  9% mình sạc 65p là 100% máy  ko ấm luôn  cpu 730g được trang bị củng tầm chơi game mượt máy sử lí ok .còn cái ko thích là hây bị lõi bỏ qua đem là ko sử dung vân tay và khuôn mặt được chỉ dùng mật khẩu để mở camera bố trí như vậy mình củng thấy ko đẹp lấm còn khoản trống ( phải như bố trí thêm đo nhịp tim hây gì nữa chắc nhìn đẹp hơn điện thoại củng là thời trang)', '', '4', 32, '2020-04-22 15:25:34', NULL, NULL),
(9924, 'Nguyễn thế bảo', 'Mọi thứ điều rất tốt pin trâu , chụp ảnh đẹp , chơi game mượt, thiết kế đẹp hơi buồn xíu vì khung và vỏ bằng nhựa nhưng lại rất đẹp nhẹ . Cho 5 sao', '', '5', 32, '2020-04-22 15:25:34', NULL, NULL),
(9925, 'Hà mạnh', 'Cũng bình thường pin xuống hơi nhanh còn lại mọi thứ ok hi vọng giữ giá đc lâu... ...', '', '4', 32, '2020-04-22 15:25:34', NULL, NULL),
(9926, 'Nguyễn Thành Nam', 'Mới mua máy hơn một tuần sài cũng không hài lòng lắm, pin tụt càng ngày càng nhanh, cấu hình tệ hơn oppo k3 trước của mình sài con chip snap 710, camera tạm ổn sắc nét hơn a50 mình dùng trc đây nhưng mình lại thích ảo giống a50 hơn, vỏ nhựa kém sang tuy nhiên đở nóng máy khi chơi game. Trọng lượng máy nhẹ, mỏng, cầm vừa tay. Thái độ nhân viên phục vụ luôn làm mình hài lòng.', '', '3', 32, '2020-04-22 15:25:34', NULL, NULL),
(9927, 'Hảo', 'Lỗi sạc lên kiểm tra 2l yêu cầu đổi máy . Nhân viên cứ bảo máy bình thường . Trong khi sạc 4 tiếng chỉ dc 40% pin . Lên kiểm tra sạc dc 5p bảo máy bình thường', '', '1', 32, '2020-04-22 15:25:34', NULL, NULL),
(9928, 'Nguyễn Huỳnh Nam', 'Cho em hỏi sao cứ sạc đến 1 lúc pin tự đứng lại và ko chịu lên nữa..nãy 93 h còn 92', NULL, '5', 32, '2020-04-22 15:25:34', NULL, NULL),
(9929, 'Hùng Chử', 'Về cấu hình thì ổn nhưng thiết kế vỏ nhựa chưa xứng tầm với giá, pin ghi 4500 nhưng kiểm tra qua nhiều phương pháp đo không hiểu sao chỉ báo có 4370 và sụt hơi nhanh, ko sử dụng để qua đêm khoảng 6h tụt mất 6-9% không biết là tại sao nữa! Các đt như J7 pro, A7 2018 để qua đêm pin chỉ tụt 1-2%. Pin ko đc tốt như giới thiêu.', '', '3', 32, '2020-04-22 15:25:34', NULL, NULL),
(9930, 'Lê thái tâm', 'Về cấu hình thì ok,pin tụt hơi nhanh.nhận diện khuôn mặt chậm,vân tay thì ok.chụp ảnh sắc nét.thiết kế đẹp...', '', '3', 32, '2020-04-22 15:25:34', NULL, NULL),
(9931, 'Quach huong', 'Thấy pin dùng rất lâu sạt cũng nhanh nữa. Hời ơi mua online bữa trước giảm giá hôm sau lên lại giá củ hên ghê.mình thấy dùng ok lắm rất mượt máy pin lại trâu okokok chơi game thỏa thích luôn ạ', '', '5', 32, '2020-04-22 15:25:34', NULL, NULL);
